import Link from "next/link";
import React, { Component } from "react";
import "../../scss/category_item.scss";
class Category_item extends React.Component {
  get_youtube_image = () => {
    return {
      backgroundImage: `url(https://img.youtube.com/vi/FOODhMCWJsc/0.jpg)` //${Background}
    };
  };

  hoverHandler = isHover => {
    this.childIsHover = isHover;
  };

  overlay = () => {
    conosole.log("HOVER");
  };

  render() {
    return (
      <div>
        <div>
          <a className="cardlink">
            <div className="cardwrapper" onHover={this.hoverHandler()}>
              <div
                className="cardimagediv"
                style={this.get_youtube_image()}
                overlay={this.childIsHover}
              >
                <img
                  className="cardimage"
                  src="https://img.youtube.com/vi/FOODhMCWJsc/0.jpg"
                  alt="test"
                />
              </div>

              <div className="carddata">
                <h5 className="title">Naslov</h5>

                <h6 className="author">Izvajalec</h6>
                <div className="views">1552 ogledov</div>
              </div>
            </div>
          </a>
        </div>
      </div>
    );
  }
}

export default Category_item;
